$("%%selector%%").each(function(){var a=this;$.ajax({type:"POST",url:"%%handler%%",data:{options:$(a).data()},success:function(c,d,b){widget_replace(a,c)},error:function(b,d,c){if($(a).data().errors==true){widget_replace(a,"{{error}}")}},})});widget_replace=function(b,a){if($(b).is("br")){$(b).replaceWith(a)}else{$(b).html(a)}};